mapper.insert:
insert into kms_a_key (key_id, key_name, key_alg, length, check_value, key_value, create_by, template_id,
                       org_id, status, mode)
values (#{keyId}, #{keyName}, #{keyAlg}, #{length}, #{checkValue}, #{keyValue}, #{createBy}, #{templateId}, #{orgId}, #{status}, #{mode});

mapper.select:
select kak.* from kms_a_key kak , kms_collection_key kck where kak.org_id=#{orgId}  and kak.status=2
and kak.key_id = kck.key_id and kck.collection_id=#{collectionId}
order by key_name;
